import { Component, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { ShortenedUrls } from 'src/app/models/ShortenedUrl';
import { ApiService } from 'src/app/services/api.service';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})

export class HomeComponent implements OnInit {
  @ViewChild(MatPaginator) paginator !: MatPaginator;

  public shortenedUrls$: any = [];
  public observable$: any;
  displayedColumns: string[] = ['Id', 'LongUrl', 'Shortened', 'Action'];
  dataSource: any;
  createUrlForm!: FormGroup;

  constructor(private auth: AuthService, private api: ApiService, private fb: FormBuilder) {}

  ngOnInit(): void {
    this.api.getShortenedUrls()
    .subscribe(res =>
      {
        this.shortenedUrls$ = res;
        this.dataSource = new MatTableDataSource<ShortenedUrls>(this.shortenedUrls$);
        this.dataSource.paginator = this.paginator;

        this.createUrlForm = this.fb.group({
          url: ['', Validators.required],
        });
      });
  }

  logOut(){
    this.auth.signOut();
  }

  addNewUrl(){
    if (this.createUrlForm.valid){
      this.api.addUrl(this.createUrlForm.value)  
    }
  }
}
