import { Component, OnInit } from '@angular/core';
import { ToastrService } from 'ngx-toastr';
import { AuthService } from 'src/app/services/auth.service';
import { NgModule } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.css']
})
export class AboutComponent implements OnInit {

  // could be easily obtained from web api
  description: string = "tyty";
  form!: FormGroup;
  constructor(private auth: AuthService, private toast: ToastrService){}

  ngOnInit(): void {
    this.form = new FormGroup({
      description: new FormControl(this.description)
    });

    this.form.get('description')?.valueChanges.subscribe((res) =>{
      this.description = res;
    });
  }

  onSubmit(){
    this.description = this.form.value;
  }

  isAdmin(): boolean{
    return this.auth.isAdmin();
  }
}
